mapper.insert:
insert into password_reset_token(
  token_hash,
  email,
  expires_at
) values(
  #{tokenHash},
  #{email},
  #{expiresAt}
)

mapper.update:
update `user`
set pending_email = #{newEmail},
    email_token_hash = #{tokenHash},
    pending_expires_at = #{expiresAt}
where user_id = #{userId}

