mapper.select:
select 
  order_id,
  lpad(cast(order_number as char), 4, '0') as order_number,
  total_price_incl AS totalPrice,
  name,
  shipping_status,
  payment_status,
  created_at
from `order`


    and (
     replace(name,' ','') like concat('%', #{req.keyword}, '%')
     or order_number like concat('%', #{req.keyword}, '%')
    )


    and shipping_status = #{req.shippingStatus}


    and payment_status = #{req.paymentStatus}


    AND created_at >= #{req.createdFrom} 


    AND created_at <= #{req.createdTo} 


order by ${req.sortField.field} ${req.sortDirection}
limit #{limit} offset #{offset}

select count(*)
from `order`


    and (
     replace(name,' ','') like concat('%', #{keyword}, '%')
     or order_number like concat('%', #{keyword}, '%')
    )


    and shipping_status = #{shippingStatus}


    and payment_status = #{paymentStatus}


    AND created_at >= #{createdFrom} 


    AND created_at <= #{createdTo}

select * from product where product_id in
#{id}
for update

mapper.update:
update order_item
set qty = #{qty},
    subtotal_incl = #{subtotal}
where order_id = #{orderId}
  and product_id = #{productId}

update product
set stock = stock + #{qty}
where product_id = #{productId}

update `order` o
join (
  select order_id, sum(qty) as sum_qty, sum(subtotal_incl) as sum_price
  from order_item
  where order_id = #{orderId}
) oi on o.order_id = oi.order_id
set
  o.total_qty = oi.sum_qty,
  o.total_price_incl = oi.sum_price
where o.order_id = #{orderId}

mapper.delete:
delete from order_item
where order_id = #{orderId}
  and product_id = #{productId}
