mapper.select:
select count(*)
from vw_product_core



      sku LIKE CONCAT('%', #{kw}, '%')
      OR product_name LIKE CONCAT('%', #{kw}, '%')



    AND price_excl >= #{minPrice}


    AND price_excl <= #{maxPrice}


    AND available >= #{minAvailable}


    AND available <= #{maxAvailable}


    AND created_at >= #{createdFrom} 


    AND created_at < #{createdTo} 


    AND updated_at >= #{updatedFrom} 


    AND updated_at < #{updatedTo} 


    AND status = #{status}

select 
  product_id,
  sku,
  product_name,
  price_excl,
  available,
  status,
  updated_at
from vw_product_core



      sku LIKE CONCAT('%', #{kw}, '%')
      OR product_name LIKE CONCAT('%', #{kw}, '%')



    AND price_excl >= #{req.minPrice}


    AND price_excl <= #{req.maxPrice}


    AND available >= #{req.minAvailable}


    AND available <= #{req.maxAvailable}


    AND created_at >= #{req.createdFrom}


    AND created_at < #{req.createdTo}


    AND updated_at >= #{req.updatedFrom}


    AND updated_at < #{req.updatedTo}


    AND status = #{req.status}


order by
  ${req.sortField.field} ${req.sortDirection},
  product_id ASC
limit #{limit}
offset #{offset}
