mapper.select:
select count(*)
from vw_product_core p



      p.sku LIKE CONCAT('%', #{kw}, '%')
      OR p.product_name LIKE CONCAT('%', #{kw}, '%')



    AND p.price_excl >= #{minPrice}


    AND p.price_excl <= #{maxPrice}


    AND p.available >= #{minAvailable}


    AND p.available <= #{maxAvailable}


    AND p.created_at >= #{createdFrom} 


    AND p.created_at <= #{createdTo} 


    AND p.updated_at >= #{updatedFrom} 


    AND p.updated_at <= #{updatedTo} 


    AND p.status = #{status}

select *
from vw_product_core p



      p.sku LIKE CONCAT('%', #{kw}, '%')
      OR p.product_name LIKE CONCAT('%', #{kw}, '%')



    AND p.price_excl >= #{req.minPrice}


    AND p.price_excl <= #{req.maxPrice}


    AND p.available >= #{req.minAvailable}


    AND p.available <= #{req.maxAvailable}


    AND p.created_at >= #{req.createdFrom}


    AND p.created_at <= #{req.createdTo}


    AND p.updated_at >= #{req.updatedFrom}


    AND p.updated_at <= #{req.updatedTo}


    AND p.status = #{req.status}


order by
  ${req.sortField.field} ${req.sortDirection},
  p.product_id ASC
limit #{limit}
offset #{offset}
