mapper.select:
select count(*)
from product p



      p.sku LIKE CONCAT('%', #{kw}, '%')
      OR p.product_name LIKE CONCAT('%', #{kw}, '%')



    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}


    AND p.stock >= #{minStock}


    AND p.stock <= #{maxStock}


    AND p.created_at >= #{createdFrom} 


    AND p.created_at <= #{createdTo} 


    AND p.updated_at >= #{updatedFrom} 


    AND p.updated_at <= #{updatedTo} 


    AND p.status = #{status.code}

select *
from product p



      p.sku LIKE CONCAT('%', #{kw}, '%')
      OR p.product_name LIKE CONCAT('%', #{kw}, '%')



    AND p.price >= #{req.minPrice}


    AND p.price <= #{req.maxPrice}


    AND p.stock >= #{req.minStock}


    AND p.stock <= #{req.maxStock}


    AND p.created_at >= #{req.createdFrom}


    AND p.created_at <= #{req.createdTo}


    AND p.updated_at >= #{req.updatedFrom}


    AND p.updated_at <= #{req.updatedTo}


    AND p.status = #{req.status.code}


order by
  ${req.sortField.field} ${req.sortOrder},
  p.product_id ASC
limit #{limit}
offset #{offset}
